import 'dart:convert';import 'todos.dart';import 'package:flutter/material.dart';import 'package:http/http.dart';import 'package:users/users.dart';import "dart:collection";void main() {  runApp(MaterialApp(    initialRoute: "/home",    routes: {      "/home": (context) => const MyApp(),      "/users": (context) => const Users()    },    debugShowCheckedModeBanner: false,  ));}class MyApp extends StatefulWidget {  const MyApp({Key? key}) : super(key: key);  @override  State<MyApp> createState() => MyAppState();}List<todos> todo = [];List<int> user = [];int id = 0;class MyAppState extends State<MyApp> {  getData() async {    Response res =        await get(Uri.parse('https://jsonplaceholder.typicode.com/todos'));    List data = jsonDecode(res.body);    for (int i = 0; i < data.length; i++) {      Map<String, dynamic> userMap = data[i];      todos t = todos.fromMap(userMap);      todo.add(t);    }    for (var e in todo) {      user.add(e.userId);    }    user = LinkedHashSet<int>.from(user).toList();    setState(() {});  }  @override  void initState() {    // TODO: implement initState    super.initState();    getData();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: Colors.black,        title: const Text(          'Users',          style: TextStyle(fontSize: 30, color: Colors.amber),        ),      ),      body: Container(          width: MediaQuery.of(context).size.width,          height: MediaQuery.of(context).size.height,          color: Colors.grey,          child: SingleChildScrollView(            child: Column(              children: user                  .map(                    (ele) => InkWell(                      child: Container(                        decoration: BoxDecoration(                            color: Colors.black,                            borderRadius: BorderRadius.circular(20)),                        padding: const EdgeInsets.all(10),                        margin: const EdgeInsets.all(7),                        alignment: Alignment.center,                        child: Text(                          'user $ele Data',                          style: const TextStyle(                            fontSize: 35,                            color: Colors.amber,                          ),                        ),                      ),                      onTap: () {                        id = ele;                        Navigator.pushNamed(context, "/users");                      },                    ),                  )                  .toList(),            ),          )),    );  }}